import React, { useEffect, useState } from 'react';
import { ShoppingCart } from 'lucide-react';
import axios from 'axios';
import { useAuth } from '../../contexts/AuthContext';
import toast from 'react-hot-toast';
import { useNavigate } from 'react-router-dom';

const CustomerDashboard = ({ user }) => {
  const { user: authUser } = useAuth();
  const navigate = useNavigate();
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [orderModal, setOrderModal] = useState({ open: false, product: null });
  const [orderQty, setOrderQty] = useState(1);
  const [orderLoading, setOrderLoading] = useState(false);
  const [orderSuccess, setOrderSuccess] = useState('');
  const [orderError, setOrderError] = useState('');
  const [orderFieldErrors, setOrderFieldErrors] = useState({});
  const [lastOrderPayload, setLastOrderPayload] = useState(null);
  const [lastOrderResponse, setLastOrderResponse] = useState(null);
  const [lastOrderError, setLastOrderError] = useState(null);
  const [phone, setPhone] = useState('');
  const [street, setStreet] = useState('');
  const [barangay, setBarangay] = useState('');
  const [city, setCity] = useState('');
  const [province, setProvince] = useState('');
  const [country, setCountry] = useState('Philippines');

  // Sample city/municipality to ZIP code mapping (expand as needed)
  const cityZipMap = {
    'Makati': '1229',
    'Quezon City': '1100',
    'Manila': '1000',
    'Cebu City': '6000',
    'Davao City': '8000',
    'Pontevedra': '6105',
    // Add more as needed
  };

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const res = await axios.get('/api/products');
        setProducts(res.data.data.products || []);
      } catch (err) {
        setError('Failed to load products.');
      } finally {
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  // Auto-fill ZIP code when city/municipality changes
  useEffect(() => {
    const zip = cityZipMap[city.trim()];
    if (zip) {
      setZipCode(zip);
    }
  }, [city]);

  // Auto-format phone number to +63 if user starts with 09
  const handlePhoneChange = (e) => {
    let value = e.target.value;
    if (value.startsWith('09')) {
      value = '+63' + value.slice(1);
    }
    setPhone(value);
  };

  const openOrderModal = (product) => {
    setOrderModal({ open: true, product });
    setOrderQty(1);
    setOrderSuccess('');
    setOrderError('');
    setOrderFieldErrors({});
    setPhone('');
    setStreet('');
    setBarangay('');
    setCity('');
    setProvince('');
    setCountry('Philippines');
  };

  const closeOrderModal = () => {
    setOrderModal({ open: false, product: null });
    setOrderQty(1);
    setOrderSuccess('');
    setOrderError('');
    setOrderFieldErrors({});
    // If order was successful, redirect to orders page
    if (orderSuccess) {
      navigate('/orders');
    }
  };

  const handleOrder = async (e) => {
    e.preventDefault();
    setOrderLoading(true);
    setOrderSuccess('');
    setOrderError('');
    setOrderFieldErrors({});
    setLastOrderPayload(null);
    setLastOrderResponse(null);
    setLastOrderError(null);
    try {
      if (!authUser || !authUser.email) {
        setOrderError('You must be logged in to place an order.');
        toast.error('You must be logged in to place an order.');
        setOrderLoading(false);
        return;
      }
      const product = orderModal.product;
      const items = [
        {
          productId: product.productId,
          productName: product.name,
          quantity: orderQty,
          unitPrice: product.price,
          totalPrice: orderQty * product.price
        }
      ];
      const customerInfo = {
        firstName: authUser?.firstName || '',
        lastName: authUser?.lastName || '',
        email: authUser?.email || '',
        phone: phone
      };
      const shippingAddress = {
        street,
        barangay,
        city,
        province,
        country: 'Philippines'
      };
      const payload = { customerInfo, items, shippingAddress };
      setLastOrderPayload(payload);
      const response = await axios.post('/api/orders', payload);
      setLastOrderResponse(response.data);
      setOrderSuccess('Order placed successfully!');
      toast.success('Order placed successfully!');
    } catch (err) {
      setLastOrderError(err);
      console.error('Order placement error:', err, err.response);
      if (err.response?.data?.errors && Array.isArray(err.response.data.errors)) {
        const fieldErrors = {};
        err.response.data.errors.forEach(errorObj => {
          if (errorObj.field && errorObj.message) {
            fieldErrors[errorObj.field] = errorObj.message;
          }
        });
        setOrderFieldErrors(fieldErrors);
        setOrderError(null);
      } else {
        setOrderError(
          err.response?.data?.message || 'Failed to place order.'
        );
        toast.error(err.response?.data?.message || 'Failed to place order.');
      }
    } finally {
      setOrderLoading(false);
    }
  };

  return (
    <div className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <h1 className="text-3xl font-bold text-gray-900 mb-4">Welcome, {user?.firstName}!</h1>
      <div className="bg-white rounded-lg shadow p-6 flex items-center mb-8">
        <div className="p-2 bg-blue-100 rounded-lg"><ShoppingCart className="h-6 w-6 text-blue-600" /></div>
        <div className="ml-4">
          <p className="text-sm font-medium text-gray-600">Your Orders</p>
          <a href="/orders" className="text-blue-600 hover:underline">View Orders</a>
        </div>
      </div>
      <div className="bg-white rounded-lg shadow p-6">
        <h2 className="text-xl font-bold mb-4">Available Products</h2>
        {loading ? (
          <div>Loading products...</div>
        ) : error ? (
          <div className="text-red-500">{error}</div>
        ) : products.length === 0 ? (
          <div className="text-gray-500">No products available.</div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {products.map(product => (
              <div key={product._id || product.productId} className="border rounded-lg p-4 flex flex-col justify-between">
                <div>
                  <h3 className="text-lg font-semibold text-gray-900">{product.name}</h3>
                  <p className="text-sm text-gray-600 mb-1">Category: {product.category}</p>
                  <p className="text-sm text-gray-600 mb-1">Price: <span className="font-bold">₱{Number(product.price).toFixed(2)}</span></p>
                  <p className="text-sm text-gray-600 mb-1">Stock: {product.stockQuantity}</p>
                </div>
                <button
                  className="mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition disabled:opacity-50"
                  onClick={() => openOrderModal(product)}
                  disabled={product.stockQuantity < 1}
                >
                  {product.stockQuantity < 1 ? 'Out of Stock' : 'Order'}
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
      {/* Order Modal */}
      {orderModal.open && orderModal.product && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-30">
          <div className="bg-white rounded-lg shadow-lg p-8 w-full max-w-md relative">
            <button
              className="absolute top-2 right-2 text-gray-400 hover:text-gray-700"
              onClick={closeOrderModal}
            >
              &times;
            </button>
            <h3 className="text-xl font-bold mb-4">Order {orderModal.product.name}</h3>
            <form onSubmit={handleOrder} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Quantity</label>
                <input
                  type="number"
                  min="1"
                  max={orderModal.product.stockQuantity}
                  value={orderQty}
                  onChange={e => setOrderQty(Number(e.target.value))}
                  className="w-full border rounded-md px-3 py-2"
                  required
                  disabled={!!orderSuccess}
                />
                <p className="text-xs text-gray-500 mt-1">Available: {orderModal.product.stockQuantity}</p>
                {orderFieldErrors['items.0.quantity'] && <div className="text-red-500 text-sm mt-1">{orderFieldErrors['items.0.quantity']}</div>}
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Phone</label>
                <input
                  type="text"
                  value={phone}
                  onChange={handlePhoneChange}
                  className="w-full border rounded-md px-3 py-2"
                  required
                  pattern="^(\+63)9\d{9}$"
                  placeholder="e.g. +639171234567"
                  disabled={!!orderSuccess}
                />
                {orderFieldErrors['customerInfo.phone'] && <div className="text-red-500 text-sm mt-1">{orderFieldErrors['customerInfo.phone']}</div>}
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Street Address</label>
                <input
                  type="text"
                  value={street}
                  onChange={e => setStreet(e.target.value)}
                  className="w-full border rounded-md px-3 py-2"
                  required
                  placeholder="e.g. Prk. Bougainvilla"
                  disabled={!!orderSuccess}
                />
                {orderFieldErrors['shippingAddress.street'] && <div className="text-red-500 text-sm mt-1">{orderFieldErrors['shippingAddress.street']}</div>}
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Barangay</label>
                <input
                  type="text"
                  value={barangay}
                  onChange={e => setBarangay(e.target.value)}
                  className="w-full border rounded-md px-3 py-2"
                  required
                  placeholder="e.g. Brgy. Canroma"
                  disabled={!!orderSuccess}
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">City/Municipality</label>
                <input
                  type="text"
                  value={city}
                  onChange={e => setCity(e.target.value)}
                  className="w-full border rounded-md px-3 py-2"
                  required
                  disabled={!!orderSuccess}
                  placeholder="e.g. Pontevedra"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Province</label>
                <input
                  type="text"
                  value={province}
                  onChange={e => setProvince(e.target.value)}
                  className="w-full border rounded-md px-3 py-2"
                  required
                  disabled={!!orderSuccess}
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Country</label>
                <input
                  type="text"
                  value="Philippines"
                  readOnly
                  className="w-full border rounded-md px-3 py-2 bg-gray-100"
                />
              </div>
              {orderSuccess && (
                <div className="text-green-600 text-sm mb-2">{orderSuccess}</div>
              )}
              {orderError && (
                <div className="text-red-500 text-sm mb-2">{orderError}</div>
              )}
              <button
                type="submit"
                className="w-full px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition"
                disabled={orderLoading || !!orderSuccess}
              >
                {orderLoading ? 'Placing Order...' : 'Place Order'}
              </button>
              {(orderSuccess || orderError) && (
                <button
                  type="button"
                  className="w-full px-4 py-2 bg-gray-300 text-gray-800 rounded hover:bg-gray-400 transition mt-2"
                  onClick={closeOrderModal}
                >
                  Close
                </button>
              )}
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default CustomerDashboard;
